from collections import defaultdict
def longestPalindrome(s):
    freq = defaultdict(int)
    for char in s:
        freq[char] += 1
    found_odd = False
    count = 0
    for char in freq:
        cur = freq[char]
        if cur % 2 == 0:
            count += cur
        else:
            found_odd = True
            count += cur - 1
    if found_odd:
        return count + 1 
    return count

        

s=""
print(longestPalindrome(s))